Ext.define('MyApp.controller.GestionarPerfil.MainController', {
    extend: 'Ext.app.Controller',

    stores: ['GestionarPerfil.MainStore'],

    models: ['GestionarPerfil.MainModel'],

    views: ['GestionarPerfil.Edit', 'GestionarPerfil.List'],

    refs: [
    	{
            ref: 'GestionarPerfilPanel',
            selector: 'panel'
        },
        {
            ref: 'GestionarPerfilList',
            selector: 'GestionarPerfilList'
        }
    ],

    init: function() {
    
        Ext.getStore('GestionarPerfil.MainStore').addListener('beforeload',this.setExtraParams,this);
    
        this.control({
            'GestionarPerfilList dataview': {
                itemdblclick: this.editRecord
            },
            'GestionarPerfilList button[action=add]': {
            	click: this.editRecord
            },
            'GestionarPerfilList button[action=delete]': {
                click: this.deleteRecord
            },
            'GestionarPerfilEdit button[action=save]': {
                click: this.updateRecord
            },
            'GestionarPerfilList button[action=filter]': {
                click: this.refreshGrid
            },
            'GestionarPerfilList button[action=clearFilter]': {
                click: this.resetForm
            },
            'GestionarPerfilList combobox': {
                change: this.refreshGrid
            },
            'GestionarPerfilList textfield': {
            	specialkey: this.onKeyEnter
            }
        });
    },

    editRecord: function(grid, record) {
        var edit = Ext.create('MyApp.view.GestionarPerfil.Edit').show();
        if(record.data){
        	edit.down('form').loadRecord(record);
        	edit.setTitle('Editar');
        }else{
        	edit.setTitle('Agregar');
        }
    },
    
    updateRecord: function(button) {
    	
        var win = button.up('window');
        var form = win.down('form');
        var record = form.getRecord();
        var values = form.getValues();
        var store = Ext.getStore('GestionarPerfil.MainStore');
        
        if (form.isValid()) {
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de GRABAR el registro?',function (btn) {
	            
	            if (btn == 'yes') {
	        		if (values.id_perfil > 0){
	        			values.tokenField = 'update';
	        			record.set(values);
	        		} else{
	        			record = Ext.create('MyApp.model.GestionarPerfil.MainModel');
	        			values.tokenField = 'insert';
	        			record.set(values);
	        			store.add(record);
	        		}
	                	
	        		store.sync({
	        	        success: function (batch, eOpts) {
				        	store.load();
				        	win.close();
	        	        },
	        	        failure: function (batch, eOpts) {
							store.rejectChanges();
	        	        }
	        	    });
	            }
	        });
		}
    },
    
    deleteRecord: function(button) {
    	
    	var grid = this.getGestionarPerfilList();
    	var record = grid.getSelectionModel().getSelection(); 
    	var store = Ext.getStore('GestionarPerfil.MainStore');
    	
    	if(record.length > 0){
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de ELIMINAR el registro?',function (btn) {
	                if (btn == 'yes') {
	             	   store.remove(record);
						store.sync({
					        success: function (batch, eOpts) {
					        	store.load();
					        },
		        	        failure: function (batch, eOpts) {
								store.rejectChanges();
		        	        }
					    });
	                }
	        });
    	}

    },
    refreshGrid: function() {
    	var store = Ext.getStore('GestionarPerfil.MainStore');
    	store.load({start:0});
    },
    resetForm: function() {
    	var store = Ext.getStore('GestionarPerfil.MainStore');
        var form = Ext.getCmp('form_filter_GestionarPerfil');
        form.reset();
    	store.load({start:0});
    },
    onKeyEnter: function(field, event, options) {
        if (event.getKey() == event.ENTER) {
        	var store = Ext.getStore('GestionarPerfil.MainStore');
        	store.load({start:0});
        }
    },
    setExtraParams: function(field, event, options) {
    	var store = Ext.getStore('GestionarPerfil.MainStore');
        var form = Ext.getCmp('form_filter_GestionarPerfil');
        var values = form.getValues();
    	store.getProxy().extraParams = values;
    }
});