Ext.define('MyApp.controller.GestionarUsuario.UsuarioPerfilSubCrudController', {
    extend: 'Ext.app.Controller',

    stores: ['GestionarUsuario.UsuarioPerfilSubCrudStore'],

    models: ['GestionarUsuario.UsuarioPerfilSubCrudModel'],

    views: ['GestionarUsuario.UsuarioPerfilSubCrudEdit', 'GestionarUsuario.UsuarioPerfilSubCrudList'],

    refs: [
    	{
            ref: 'UsuarioPerfilSubCrudPanel',
            selector: 'panel'
        },
        {
            ref: 'UsuarioPerfilSubCrudList',
            selector: 'UsuarioPerfilSubCrudList'
        }
    ],

    init: function() {
        this.control({
            'UsuarioPerfilSubCrudList dataview': {
                itemdblclick: this.editRecord
            },
            'UsuarioPerfilSubCrudList button[action=add]': {
            	click: this.editRecord
            },
            'UsuarioPerfilSubCrudList button[action=delete]': {
                click: this.deleteRecord
            },
            'UsuarioPerfilSubCrudEdit button[action=save]': {
                click: this.updateRecord
            }
        });
    },

    editRecord: function(grid, record) {
        var edit = Ext.create('MyApp.view.GestionarUsuario.UsuarioPerfilSubCrudEdit').show();
        if(record.data){
        	edit.down('form').loadRecord(record);
        	edit.setTitle('Editar');
        }else{
        	edit.setTitle('Agregar');
        }
    },
    
    updateRecord: function(button) {
    	
        var win = button.up('window');
        var form = win.down('form');
        var record = form.getRecord();
        var values = form.getValues();
        var store = Ext.getStore('GestionarUsuario.UsuarioPerfilSubCrudStore');
        
        var id_perfil = form.getForm().findField('id_perfil');
        values.nombre_perfil = id_perfil.getRawValue();
        
        var id_estado = form.getForm().findField('id_estado');
        values.descrip_estado = id_estado.getRawValue();
        
        
        if (form.isValid()) {
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de GRABAR el registro?',function (btn) {
	            
	            if (btn == 'yes') {
                
                    if(win.title === 'Editar') {
                        record.set(values);
                    } else{
                        record = Ext.create('MyApp.model.GestionarUsuario.UsuarioPerfilSubCrudModel');
                        record.set(values);
                        store.add(record);
                    }

                    win.close();	
                    store.sync({
                        success: function (batch, eOpts) {
                            store.load({params: {id_usuario: null}});
                        }
                    });
	            }
	            
	     });
        
        }
    },
    deleteRecord: function(button) {
    	
    	var grid = this.getUsuarioPerfilSubCrudList();
    	var record = grid.getSelectionModel().getSelection(); 
    	var store = Ext.getStore('GestionarUsuario.UsuarioPerfilSubCrudStore');
    	
    	if(record.length > 0){
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de ELIMINAR el registro?',function (btn) {
	                if (btn == 'yes') {
	             	   store.remove(record);
						store.sync({
					        success: function (batch, eOpts) {
					            store.load({params: {id_usuario: null}});
					        }
					    });
	                }
	        });
    	}

    }
    
});