    editRecord: function(grid, record) {
        var edit = Ext.create('MyApp.view.MttoCuentaPorCobrar.Edit').show();
        var form = edit.down('form');
        if(record.data){
        	edit.down('form').loadRecord(record);
        	edit.setTitle('Editar');
        	
        	var subModel_CuentaAmortizacionQuery = Ext.getCmp('subModel_CuentaAmortizacionQuery');
        	var id_cuenta_x_cobrar = form.getForm().findField('id_cuenta_x_cobrar');
        	
			subModel_CuentaAmortizacionQuery.store.reload({params: {id_cuenta_x_cobrar: id_cuenta_x_cobrar.getValue(), start:0, limit:10}});
			
        }else{
        	edit.setTitle('Agregar');
        	var subModel_CuentaAmortizacionQuery = Ext.getCmp('subModel_CuentaAmortizacionQuery');
			subModel_CuentaAmortizacionQuery.store.reload({params: {id_cuenta_x_cobrar: -1, start:0, limit:10}});
        }
    },
    
    updateRecord: function(button) {
    	
        var win = button.up('window');
        var form = win.down('form');
        var record = form.getRecord();
        var values = form.getValues();
        var store = Ext.getStore('MttoCuentaPorCobrar.MainStore');
        
        if (form.isValid()) {
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de GRABAR el registro?',function (btn) {
	            
	            if (btn == 'yes') {
	        		if (values.id_cuenta_x_cobrar > 0){
	        			values.tokenField = 'update';
	        			record.set(values);
	        		} else{
	        			record = Ext.create('MyApp.model.MttoCuentaPorCobrar.MainModel');
	        			values.tokenField = 'insert';
	        			record.set(values);
	        			store.add(record);
	        		}
	                
	        		store.sync({
	        	        success: function (batch, eOpts) {
				        	store.load();
				        	win.close();
	        	        },
	        	        failure: function (batch, eOpts) {
							store.rejectChanges();
	        	        }
	        	    });
	            }
	        });
		}

    },
    
    deleteRecord: function(button) {
    	
    	var grid = this.getMttoCuentaPorCobrarList();
    	var record = grid.getSelectionModel().getSelection(); 
    	var store = Ext.getStore('MttoCuentaPorCobrar.MainStore');
    	
    	if(record.length > 0){
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de ELIMINAR el registro?',function (btn) {
	                if (btn == 'yes') {
	             	   store.remove(record);
						store.sync({
					        success: function (batch, eOpts) {
					        	store.load();
					        }
					    });
	                }
	        });
    	}

    },
    refreshGrid: function() {
    	var store = Ext.getStore('MttoCuentaPorCobrar.MainStore');
    	store.load({start:0});
    },
