Ext.define('MyApp.controller.GestionarDocFactura.MainController', {
    extend: 'Ext.app.Controller',

    stores: ['GestionarDocFactura.MainStore'],

    models: ['GestionarDocFactura.MainModel'],

    views: ['GestionarDocFactura.Edit', 'GestionarDocFactura.List'],

    refs: [
    	{
            ref: 'GestionarDocFacturaPanel',
            selector: 'panel'
        },
        {
            ref: 'GestionarDocFacturaList',
            selector: 'GestionarDocFacturaList'
        }
    ],

    init: function() {
    
        Ext.getStore('GestionarDocFactura.MainStore').addListener('beforeload',this.setExtraParams,this);
        
        this.control({
            'GestionarDocFacturaList dataview': {
                itemdblclick: this.editRecord
            },
            'GestionarDocFacturaList button[action=add]': {
            	click: this.editRecord
            },
            'GestionarDocFacturaList button[action=delete]': {
                click: this.deleteRecord
            },
            'GestionarDocFacturaEdit button[action=save]': {
                click: this.updateRecord
            },
            'GestionarDocFacturaList button[action=filter]': {
                click: this.refreshGrid
            },
            'GestionarDocFacturaList button[action=clearFilter]': {
                click: this.resetForm
            },
            'GestionarDocFacturaList combobox': {
                change: this.refreshGrid
            },
            'GestionarDocFacturaList textfield': {
            	specialkey: this.onKeyEnter
            }
        });
    },

    editRecord: function(grid, record) {
        var edit = Ext.create('MyApp.view.GestionarDocFactura.Edit').show();
        var form = edit.down('form');
        if(record.data){
        	edit.down('form').loadRecord(record);
        	edit.setTitle('Editar');
        	
        	var subModel_DocumentoFacturaDetalleQuery = Ext.getCmp('subModel_DocumentoFacturaDetalleQuery');
        	var id_documento_venta = form.getForm().findField('id_documento_venta');
        	
			subModel_DocumentoFacturaDetalleQuery.store.reload({params: {id_documento_venta: id_documento_venta.getValue(), start:0, limit:10}});
			
        }else{
        	edit.setTitle('Agregar');
        	var subModel_DocumentoFacturaDetalleQuery = Ext.getCmp('subModel_DocumentoFacturaDetalleQuery');
			subModel_DocumentoFacturaDetalleQuery.store.reload({params: {id_documento_venta: -1, start:0, limit:10}});
        }
    },
    
    updateRecord: function(button) {
    	
        var win = button.up('window');
        var form = win.down('form');
        var record = form.getRecord();
        var values = form.getValues();
        var store = Ext.getStore('GestionarDocFactura.MainStore');
        
        if (form.isValid()) {
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de GRABAR el registro?',function (btn) {
	            
	            if (btn == 'yes') {
	        		if (values.id_documento_venta > 0){
	        			values.tokenField = 'update';
	        			record.set(values);
	        		} else{
	        			record = Ext.create('MyApp.model.GestionarDocFactura.MainModel');
	        			values.tokenField = 'insert';
	        			record.set(values);
	        			store.add(record);
	        		}
	                
	        		store.sync({
	        	        success: function (batch, eOpts) {
				        	store.load();
				        	win.close();
	        	        },
	        	        failure: function (batch, eOpts) {
							store.rejectChanges();
	        	        }
	        	    });
	            }
	        });
		}

    },
    
    deleteRecord: function(button) {
    	
    	var grid = this.getGestionarDocFacturaList();
    	var record = grid.getSelectionModel().getSelection(); 
    	var store = Ext.getStore('GestionarDocFactura.MainStore');
    	
    	if(record.length > 0){
	        Ext.Msg.confirm('Confirmar','¿Estás seguro de ELIMINAR el registro?',function (btn) {
	                if (btn == 'yes') {
	             	   store.remove(record);
						store.sync({
					        success: function (batch, eOpts) {
					        	store.load();
					        }
					    });
	                }
	        });
    	}

    },
    refreshGrid: function() {
    	var store = Ext.getStore('GestionarDocFactura.MainStore');
    	store.load({start:0});
    },
    resetForm: function() {
    	var store = Ext.getStore('GestionarDocFactura.MainStore');
        var form = Ext.getCmp('form_filter_GestionarDocFactura');
        form.reset();
    	store.load({start:0});
    },
    onKeyEnter: function(field, event, options) {
        if (event.getKey() == event.ENTER) {
        	var store = Ext.getStore('GestionarDocFactura.MainStore');
        	store.load({start:0});
        }
    },
    setExtraParams: function(field, event, options) {
    	var store = Ext.getStore('GestionarDocFactura.MainStore');
        var form = Ext.getCmp('form_filter_GestionarDocFactura');
        var values = form.getValues();
    	store.getProxy().extraParams = values;
    }
    
});