mapper.select:
select
pc.srl_id_contrato , (select a[2] || '-' || a[3] from (select regexp_split_to_array(pc.var_num_contrato, '-')) as dt(a)) as "var_num_contrato", pc.int_id_tab_tip , cnv.srl_id_convocatoria , (select a[2] || '-' || a[3] from (select regexp_split_to_array(cnv.var_num_convocatoria, '-')) as dt(a)) as "var_num_convocatoria", cao.srl_id_conv_area_org , cao.var_codigo_postulacion, to_char(pc.dte_fec_suscrip, 'DD/MM/YYYY') as "dte_fec_suscrip", to_char(pc.dte_fec_inicio, 'DD/MM/YYYY') as "dte_fec_inicio", to_char(pc.dte_fec_termino, 'DD/MM/YYYY') as "dte_fec_termino", cg.srl_id_cargo , cg.var_nom_cargo, tc.destablatipo as "des_tipo_contrato", (COALESCE(pn.nombre,pnc.nombre) || ' ' || COALESCE(pn.apepat,pnc.apepat) || ' ' || COALESCE(pn.apemat,pnc.apemat)) as "nombre_completo"
from sernanp.t_personal_contrato pc left join sernanp.t_postulante_cnv_area_org pcao on pc.srl_id_postulante_conv_area_org = pcao.srl_id_postulante_conv_area_org left join sernanp.t_convocatoria_area_org cao on pcao.srl_id_conv_area_org = cao.srl_id_conv_area_org left join sernanp.t_convocatoria cnv on cao.srl_id_convocatoria = cnv.srl_id_convocatoria left join sernanp.t_cargo cg on pc.srl_id_cargo = cg.srl_id_cargo left join sernanp.t_postulante_personal pp on pcao.int_id_postulante = pp.int_id_postulante left join sernanp.personanatural pn on pn.idpersonanatural = pp.int_id_postulante left join sernanp.persona p on pn.idpersona = p.idpersona left join sernanp.personal ppp on pc.idpersonal = ppp.idpersonal left join sernanp.personanatural pnc on ppp.idpersonanatural = pnc.idpersonanatural left join ( select det.var_cod_estado,det.var_nombre_estado from sernanp.t_detalle_estado_tabla det where det.srl_id_estado_tabla = 2 ) detx on detx.var_cod_estado = cast(pc.int_id_estado as char) left join (select tc.ordtablatipo,tc.destablatipo from sernanp.tablatipo tc where tc.idtabla = (select t.idtabla from sernanp.tabla t where t.destabla = 'Tipo Contrato')) tc on pc.int_id_tab_tip = tc.ordtablatipo where
pc.srl_id_contrato_padre is null and pc.int_id_estado = #{int_id_estado}
order by pc.dte_fec_suscrip desc;
